let _ = global.get('lodash')
let app = global.get('app')

let sog = app.getSelfPath('navigation.speedOverGround')
let acVoltage = app.getSelfPath('electrical.inverters.261.acin.voltage')
let rpm = app.getSelfPath('propulsion.port.revolutions')
let sun = app.getSelfPath('environment.sun')

let res = 0
if ( acVoltage && _.isUndefined(acVoltage.value) && sog && !_.isUndefined(sog.value) && rpm && !_.isUndefined(rpm.value) && sun && !_.isUndefined(sun.value) ) {
    res = sog.value < 0.5 && rpm.value === 0 && sun.value === 'day'
    //res = 1
}

return {payload: res }
